mapper.select:

select 

from moneyfeed.cs_attachments


     and id = #{id,jdbcType=BIGINT}


     and master_id = #{masterId,jdbcType=BIGINT}


     and real_file_name = #{realFileName,jdbcType=VARCHAR}


     and file_name = #{fileName,jdbcType=VARCHAR}


     and file_type = #{fileType,jdbcType=VARCHAR}


     and file_size = #{fileSize,jdbcType=BIGINT}


     and save_address = #{saveAddress,jdbcType=VARCHAR}


     and uri = #{uri,jdbcType=VARCHAR}


     and uploaded = #{uploaded,jdbcType=TIMESTAMP}

mapper.insert:

insert into moneyfeed.cs_attachments (id, master_id, real_file_name, 
  file_name, file_type, file_size, 
  save_address, uri, uploaded
  )
values (#{id,jdbcType=BIGINT}, #{masterId,jdbcType=BIGINT}, #{realFileName,jdbcType=VARCHAR}, 
  #{fileName,jdbcType=VARCHAR}, #{fileType,jdbcType=VARCHAR}, #{fileSize,jdbcType=BIGINT}, 
  #{saveAddress,jdbcType=VARCHAR}, #{uri,jdbcType=VARCHAR}, #{uploaded,jdbcType=TIMESTAMP}
  )

