mapper.select:

select 

from moneyfeed.cs_user_feedback_solves

  
     and id = #{id,jdbcType=BIGINT}
  
  
     and gmt_created = #{gmtCreated,jdbcType=TIMESTAMP}
  
  
     and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
  
  
     and creator = #{creator,jdbcType=BIGINT}
  
  
     and modifier = #{modifier,jdbcType=BIGINT}
  
  
     and feedback_id = #{feedbackId,jdbcType=BIGINT}
  
  
     and solve_user_id = #{solveUserId,jdbcType=BIGINT}
  
  
     and solve_time = #{solveTime,jdbcType=DATE}
  
  
     and solve_desc = #{solveDesc,jdbcType=VARCHAR}
  
  
     and solve_type = #{solveType,jdbcType=VARCHAR}
  
  
     and shop_id = #{shopId,jdbcType=BIGINT}
  
  
     and data_status = #{dataStatus,jdbcType=VARCHAR}

mapper.insert:

insert into moneyfeed.cs_user_feedback_solves (id, gmt_created, gmt_modified, 
  creator, modifier, version, feedback_id, 
  solve_user_id, solve_time, solve_desc, 
  solve_type, shop_id, data_status
  )
values (#{id,jdbcType=BIGINT}, #{gmtCreated,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}, 
  #{creator,jdbcType=BIGINT}, #{modifier,jdbcType=BIGINT}, 0, #{feedbackId,jdbcType=BIGINT}, 
  #{solveUserId,jdbcType=BIGINT}, #{solveTime,jdbcType=DATE}, #{solveDesc,jdbcType=VARCHAR}, 
  #{solveType,jdbcType=VARCHAR}, #{shopId,jdbcType=BIGINT}, #{dataStatus,jdbcType=VARCHAR}
  )

insert into moneyfeed.cs_user_feedback_solves (id, gmt_created, gmt_modified, creator, modifier, version, feedback_id, solve_user_id, solve_time, solve_desc, solve_type, shop_id, data_status ) values
(#{model.id,jdbcType=BIGINT}, #{model.gmtCreated,jdbcType=TIMESTAMP}, #{model.gmtModified,jdbcType=TIMESTAMP}, #{model.creator,jdbcType=BIGINT}, #{model.modifier,jdbcType=BIGINT}, 0, #{model.feedbackId,jdbcType=BIGINT}, #{model.solveUserId,jdbcType=BIGINT}, #{model.solveTime,jdbcType=DATE}, #{model.solveDesc,jdbcType=VARCHAR}, #{model.solveType,jdbcType=VARCHAR}, #{model.shopId,jdbcType=BIGINT}, #{model.dataStatus,jdbcType=VARCHAR}
)

mapper.update:

update moneyfeed.cs_user_feedback_solves

  
    gmt_created = #{newModel.gmtCreated,jdbcType=TIMESTAMP},
  
  
    gmt_modified = #{newModel.gmtModified,jdbcType=TIMESTAMP},
  
  
    creator = #{newModel.creator,jdbcType=BIGINT},
  
  
    modifier = #{newModel.modifier,jdbcType=BIGINT},
  
  
    feedback_id = #{newModel.feedbackId,jdbcType=BIGINT},
  
  
    solve_user_id = #{newModel.solveUserId,jdbcType=BIGINT},
  
  
    solve_time = #{newModel.solveTime,jdbcType=DATE},
  
  
    solve_desc = #{newModel.solveDesc,jdbcType=VARCHAR},
  
  
    solve_type = #{newModel.solveType,jdbcType=VARCHAR},
  
  
    shop_id = #{newModel.shopId,jdbcType=BIGINT},
  
  
    data_status = #{newModel.dataStatus,jdbcType=VARCHAR},
  
  version = version + 1


  
     and id = #{oldModel.id,jdbcType=BIGINT}
  
  
     and gmt_created = #{oldModel.gmtCreated,jdbcType=TIMESTAMP}
  
  
     and gmt_modified = #{oldModel.gmtModified,jdbcType=TIMESTAMP}
  
  
     and creator = #{oldModel.creator,jdbcType=BIGINT}
  
  
     and modifier = #{oldModel.modifier,jdbcType=BIGINT}
  
  
     and feedback_id = #{oldModel.feedbackId,jdbcType=BIGINT}
  
  
     and solve_user_id = #{oldModel.solveUserId,jdbcType=BIGINT}
  
  
     and solve_time = #{oldModel.solveTime,jdbcType=DATE}
  
  
     and solve_desc = #{oldModel.solveDesc,jdbcType=VARCHAR}
  
  
     and solve_type = #{oldModel.solveType,jdbcType=VARCHAR}
  
  
     and shop_id = #{oldModel.shopId,jdbcType=BIGINT}
  
  
     and data_status = #{oldModel.dataStatus,jdbcType=VARCHAR}

