mapper.select:

select 

from moneyfeed.cs_user_feedbacks

  
     and id = #{id,jdbcType=BIGINT}
  
  
     and gmt_created = #{gmtCreated,jdbcType=TIMESTAMP}
  
  
     and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
  
  
     and creator = #{creator,jdbcType=BIGINT}
  
  
     and modifier = #{modifier,jdbcType=BIGINT}
  
  
     and user_id = #{userId,jdbcType=BIGINT}
  
  
     and feedback_time = #{feedbackTime,jdbcType=DATE}
  
  
     and order_id = #{orderId,jdbcType=BIGINT}
  
  
     and feedback_number = #{feedbackNumber,jdbcType=VARCHAR}
  
  
     and feedback_desc = #{feedbackDesc,jdbcType=VARCHAR}
  
  
     and feedback_status = #{feedbackStatus,jdbcType=VARCHAR}
  
  
     and channel = #{channel,jdbcType=VARCHAR}
  
  
     and intention_shop_id = #{intentionShopId,jdbcType=BIGINT}
  
  
     and shop_id = #{shopId,jdbcType=BIGINT}
  
  
     and distributed_desc = #{distributedDesc,jdbcType=VARCHAR}
  
  
     and distributed_creator = #{distributedCreator,jdbcType=BIGINT}
  
  
     and distributed_created = #{distributedCreated,jdbcType=TIMESTAMP}
  
  
     and official_accounts_mobile = #{officialAccountsMobile,jdbcType=VARCHAR}
  
  
     and official_accounts_name = #{officialAccountsName,jdbcType=VARCHAR}
  
  
     and message_label = #{messageLabel,jdbcType=VARCHAR}
  
  
     and message_label_type = #{messageLabelType,jdbcType=VARCHAR}
  
  
     and data_status = #{dataStatus,jdbcType=VARCHAR}

mapper.update:

update moneyfeed.cs_user_feedbacks

  
    gmt_created = #{newModel.gmtCreated,jdbcType=TIMESTAMP},
  
  
    gmt_modified = #{newModel.gmtModified,jdbcType=TIMESTAMP},
  
  
    creator = #{newModel.creator,jdbcType=BIGINT},
  
  
    modifier = #{newModel.modifier,jdbcType=BIGINT},
  
  
    user_id = #{newModel.userId,jdbcType=BIGINT},
  
  
    feedback_time = #{newModel.feedbackTime,jdbcType=DATE},
  
  
    order_id = #{newModel.orderId,jdbcType=BIGINT},
  
  
    feedback_number = #{newModel.feedbackNumber,jdbcType=VARCHAR},
  
  
    feedback_desc = #{newModel.feedbackDesc,jdbcType=VARCHAR},
  
  
    feedback_status = #{newModel.feedbackStatus,jdbcType=VARCHAR},
  
  
    channel = #{newModel.channel,jdbcType=VARCHAR},
  
  
    intention_shop_id = #{newModel.intentionShopId,jdbcType=BIGINT},
  
  
    shop_id = #{newModel.shopId,jdbcType=BIGINT},
  
  
    distributed_desc = #{newModel.distributedDesc,jdbcType=VARCHAR},
  
  
    distributed_creator = #{newModel.distributedCreator,jdbcType=BIGINT},
  
  
    distributed_created = #{newModel.distributedCreated,jdbcType=TIMESTAMP},
  
  
