    official_accounts_name = #{newModel.officialAccountsName,jdbcType=VARCHAR},
  
  
    message_label = #{newModel.messageLabel,jdbcType=VARCHAR},
  
  
    message_label_type = #{newModel.messageLabelType,jdbcType=VARCHAR},
  
  
    data_status = #{newModel.dataStatus,jdbcType=VARCHAR},
  
  version = version + 1


  
     and id = #{oldModel.id,jdbcType=BIGINT}
  
  
     and gmt_created = #{oldModel.gmtCreated,jdbcType=TIMESTAMP}
  
  
