     and feedback_status = #{oldModel.feedbackStatus,jdbcType=VARCHAR}
  
  
     and channel = #{oldModel.channel,jdbcType=VARCHAR}
  
  
     and intention_shop_id = #{oldModel.intentionShopId,jdbcType=BIGINT}
  
  
     and shop_id = #{oldModel.shopId,jdbcType=BIGINT}
  
  
     and distributed_desc = #{oldModel.distributedDesc,jdbcType=VARCHAR}
  
  
     and distributed_creator = #{oldModel.distributedCreator,jdbcType=BIGINT}
  
  
     and distributed_created = #{oldModel.distributedCreated,jdbcType=TIMESTAMP}
  
  
