mapper.select:
select 

from moneyfeed.uc_pm_label_customer_mapping

  
     and id = #{id,jdbcType=BIGINT}
  
  
     and gmt_modified >=  #{virtualCreateTime,jdbcType=TIMESTAMP} 
  
  
     and label_id = #{labelId,jdbcType=BIGINT}
  
  
     and customer_id = #{customerId,jdbcType=BIGINT}
  
  
     and enable = #{enable,jdbcType=BIT}

mapper.update:
update moneyfeed.uc_pm_label_customer_mapping

  
    label_id = #{newModel.labelId,jdbcType=BIGINT},
  
  
    customer_id = #{newModel.customerId,jdbcType=BIGINT},
  
  
    enable = #{newModel.enable,jdbcType=BIT},
  
  gmt_modified = #{newModel.gmtModified,jdbcType=TIMESTAMP},modifier = 0,version = version + 1


  
     and id = #{oldModel.id,jdbcType=BIGINT}
  
  
     and label_id = #{oldModel.labelId,jdbcType=BIGINT}
  
  
     and customer_id = #{oldModel.customerId,jdbcType=BIGINT}
  
  
     and enable = #{oldModel.enable,jdbcType=BIT}

update moneyfeed.uc_pm_label_customer_mapping

  label_id = #{newModel.labelId,jdbcType=BIGINT},
  customer_id = #{newModel.customerId,jdbcType=BIGINT},
  enable = #{newModel.enable,jdbcType=BIT},
  gmt_modified = #{newModel.gmtModified,jdbcType=TIMESTAMP},modifier = 0,version = version + 1


  
     and id = #{oldModel.id,jdbcType=BIGINT}
  
  
     and label_id = #{oldModel.labelId,jdbcType=BIGINT}
  
  
     and customer_id = #{oldModel.customerId,jdbcType=BIGINT}
  
  
     and enable = #{oldModel.enable,jdbcType=BIT}

mapper.delete:
delete from moneyfeed.uc_pm_label_customer_mapping

  
     and id = #{id,jdbcType=BIGINT}
  
  
     and label_id = #{labelId,jdbcType=BIGINT}
  
  
     and customer_id = #{customerId,jdbcType=BIGINT}
  
  
     and enable = #{enable,jdbcType=BIT}
